using System;using System.Collections.Generic;using WebApi.Models.DTOs;using WebApi.Models.Entities;namespace WebApi.Models.DTOs{	public class RolDTO	{		public int Id { get; set; } = 0;		public string DescripcionRol { get; set; } = "";		public string FechaAlta { get; set; } = "";		public int UsuarioAltaId { get; set; } = 0;		public string? FechaBaja { get; set; } = null;		public int? UsuarioBajaId { get; set; } = null;		public string? FechaModificacion { get; set; } = null;		public int? UsuarioModificacionId { get; set; } = null;        public List<RolPermisoDTO> RolPermisos { get; set; } = new List<RolPermisoDTO>();        public List<UsuarioRolDTO> UsuarioRoles { get; set; } = new List<UsuarioRolDTO>();		public Rol ToEntity()		{			return new Rol()			{				Id = Id,				DescripcionRol = DescripcionRol,				FechaAlta = DateTime.Parse(FechaAlta),				UsuarioAltaId = UsuarioAltaId,				FechaBaja = FechaBaja != "" && FechaBaja != null ? DateTime.Parse(FechaBaja) : null,				UsuarioBajaId = UsuarioBajaId,				FechaModificacion = FechaModificacion != "" && FechaModificacion != null ? DateTime.Parse(FechaModificacion) : null,				UsuarioModificacionId = UsuarioModificacionId,         };      }        internal void validar()        {            var strError = "";            if (DescripcionRol.Trim() == "") strError += "* DescripcionRol es requerido";            if (FechaAlta.Trim() == "") strError += "* FechaAlta es requerido";            if (UsuarioAltaId == 0) strError += "* UsuarioAltaId es requerido";            if (strError != "")            {                throw new Exception(strError);            }         }   }}namespace WebApi.Models.Entities{	public partial class Rol: Idtoable	{		public override RolDTO ToDTO()		{			var dto = new RolDTO();			dto.Id = Id;			dto.DescripcionRol = DescripcionRol;			dto.FechaAlta = FechaAlta.ToString("yyyy/MM/dd");			dto.UsuarioAltaId = UsuarioAltaId;			dto.FechaBaja = FechaBaja?.ToString("yyyy/MM/dd");			dto.UsuarioBajaId = UsuarioBajaId;			dto.FechaModificacion = FechaModificacion?.ToString("yyyy/MM/dd");			dto.UsuarioModificacionId = UsuarioModificacionId;			if (RolPermisos != null)			{				var lista = new List<RolPermisoDTO>();				foreach (var item in RolPermisos)				{					item.Rol = new Rol();					lista.Add(item.ToDTO());				}				dto.RolPermisos = lista;			}			if (UsuarioRoles != null)			{				var lista = new List<UsuarioRolDTO>();				foreach (var item in UsuarioRoles)				{					item.Rol = new Rol();					lista.Add(item.ToDTO());				}				dto.UsuarioRoles = lista;			}			return dto;		}	}}