using System;using System.Collections.Generic;using WebApi.Models.DTOs;using WebApi.Models.Entities;namespace WebApi.Models.DTOs{	public class RolPermisoDTO	{		public int Id { get; set; } = 0;		public int RolId { get; set; } = 0;		public int PermisoId { get; set; } = 0;		public string FechaAlta { get; set; } = "";		public int UsuarioAltaId { get; set; } = 0;		public string? FechaBaja { get; set; } = null;		public int? UsuarioBajaId { get; set; } = null;		public string? FechaModificacion { get; set; } = null;		public int? UsuarioModificacionId { get; set; } = null;		public RolDTO? Rol { get; set; }		public PermisoDTO? Permiso { get; set; }		public RolPermiso ToEntity()		{			return new RolPermiso()			{				Id = Id,				RolId = RolId,				PermisoId = PermisoId,				FechaAlta = DateTime.Parse(FechaAlta),				UsuarioAltaId = UsuarioAltaId,				FechaBaja = FechaBaja != "" && FechaBaja != null ? DateTime.Parse(FechaBaja) : null,				UsuarioBajaId = UsuarioBajaId,				FechaModificacion = FechaModificacion != "" && FechaModificacion != null ? DateTime.Parse(FechaModificacion) : null,				UsuarioModificacionId = UsuarioModificacionId,         };      }        internal void validar()        {            var strError = "";            if (RolId == 0) strError += "* RolId es requerido";            if (PermisoId == 0) strError += "* PermisoId es requerido";            if (FechaAlta.Trim() == "") strError += "* FechaAlta es requerido";            if (UsuarioAltaId == 0) strError += "* UsuarioAltaId es requerido";            if (strError != "")            {                throw new Exception(strError);            }         }   }}namespace WebApi.Models.Entities{	public partial class RolPermiso: Idtoable	{		public override RolPermisoDTO ToDTO()		{			var dto = new RolPermisoDTO();			dto.Id = Id;			dto.RolId = RolId;			dto.PermisoId = PermisoId;			dto.FechaAlta = FechaAlta.ToString("yyyy/MM/dd");			dto.UsuarioAltaId = UsuarioAltaId;			dto.FechaBaja = FechaBaja?.ToString("yyyy/MM/dd");			dto.UsuarioBajaId = UsuarioBajaId;			dto.FechaModificacion = FechaModificacion?.ToString("yyyy/MM/dd");			dto.UsuarioModificacionId = UsuarioModificacionId;			if (Rol != null)			{				this.Rol.RolPermisos = new List<RolPermiso>();				dto.Rol = Rol.ToDTO();			}			if (Permiso != null)			{				this.Permiso.RolPermisos = new List<RolPermiso>();				dto.Permiso = Permiso.ToDTO();			}			return dto;		}	}}