using System;using System.Collections.Generic;using WebApi.Models.DTOs;using WebApi.Models.Entities;namespace WebApi.Models.DTOs{	public class UsuarioDTO	{		public int Id { get; set; } = 0;		public string Nombre { get; set; } = "";		public string UserName { get; set; } = "";		public string Password { get; set; } = "";		public string FechaAlta { get; set; } = "";		public int UsuarioAltaId { get; set; } = 0;		public string? FechaBaja { get; set; } = null;		public int? UsuarioBajaId { get; set; } = null;		public string? FechaModificacion { get; set; } = null;		public int? UsuarioModificacionId { get; set; } = null;        public List<UsuarioRolDTO> UsuarioRoles { get; set; } = new List<UsuarioRolDTO>();		public Usuario ToEntity()		{			return new Usuario()			{				Id = Id,				Nombre = Nombre,				UserName = UserName,				Password = Password,				FechaAlta = DateTime.Parse(FechaAlta),				UsuarioAltaId = UsuarioAltaId,				FechaBaja = FechaBaja != "" && FechaBaja != null ? DateTime.Parse(FechaBaja) : null,				UsuarioBajaId = UsuarioBajaId,				FechaModificacion = FechaModificacion != "" && FechaModificacion != null ? DateTime.Parse(FechaModificacion) : null,				UsuarioModificacionId = UsuarioModificacionId,         };      }        internal void validar()        {            var strError = "";            if (Nombre.Trim() == "") strError += "* Nombre es requerido";            if (UserName.Trim() == "") strError += "* UserName es requerido";            if (Password.Trim() == "") strError += "* Password es requerido";            if (FechaAlta.Trim() == "") strError += "* FechaAlta es requerido";            if (UsuarioAltaId == 0) strError += "* UsuarioAltaId es requerido";            if (strError != "")            {                throw new Exception(strError);            }         }   }}namespace WebApi.Models.Entities{	public partial class Usuario: Idtoable	{		public override UsuarioDTO ToDTO()		{			var dto = new UsuarioDTO();			dto.Id = Id;			dto.Nombre = Nombre;			dto.UserName = UserName;			dto.Password = Password;			dto.FechaAlta = FechaAlta.ToString("yyyy/MM/dd");			dto.UsuarioAltaId = UsuarioAltaId;			dto.FechaBaja = FechaBaja?.ToString("yyyy/MM/dd");			dto.UsuarioBajaId = UsuarioBajaId;			dto.FechaModificacion = FechaModificacion?.ToString("yyyy/MM/dd");			dto.UsuarioModificacionId = UsuarioModificacionId;			if (UsuarioRoles != null)			{				var lista = new List<UsuarioRolDTO>();				foreach (var item in UsuarioRoles)				{					item.Usuario = new Usuario();					lista.Add(item.ToDTO());				}				dto.UsuarioRoles = lista;			}			return dto;		}	}}